import React, { useContext, useEffect, useState } from 'react';
import { Text, Image, View, TouchableOpacity, Linking, Alert, Platform, PermissionsAndroid } from 'react-native';
import { AntDesign, FontAwesome, MaterialCommunityIcons } from "@expo/vector-icons";
import { API, graphqlOperation } from 'aws-amplify';
import AudioNotification from "react-native-audio-notification";
import RNFetchBlob from 'rn-fetch-blob';

import styles from './styles';
import { Sound } from "expo-av/build/Audio/Sound";

import { AppContext } from '../../Appcontext';
import { getSong } from "../../src/graphql/queries";


const PlayerWidget = () => {

  const [song, setSong] = useState(null);
  const [sound, setSound] = useState<Sound | null>(null);
  const [isPlaying, setIsPlaying] = useState<boolean>(true);
  const [duration, setDuration] = useState<number | null>(null);
  const [position, setPosition] = useState<number | null>(null);

  const { songId } = useContext(AppContext);

  useEffect(() => {
    const fetchSong = async () => {
      try {
        const data = await API.graphql(graphqlOperation(getSong, { id: songId }))
        setSong(data.data.getSong);
      } catch (e) {
        console.log(e);
      }
    }

    fetchSong();
  }, [songId])

  const snd = {
    title: "Secrets",
    description: "OneRepublic",
    cover: "data:image/jpeg;base64,",
    is_play: true,
    is_like: true
  }

  const notice = AudioNotification(snd);

  const onPlaybackStatusUpdate = (status) => {
    setIsPlaying(status.isPlaying);
    setDuration(status.durationMillis);
    setPosition(status.positionMillis);
    notice.update();
  }

  const playCurrentSong = async () => {
    if (sound) {
      await sound.unloadAsync();
      notice.setAudioConfig(snd);
    }

    const { sound: newSound } = await Sound.createAsync(

      { uri: song.uri },
      { shouldPlay: isPlaying },
      onPlaybackStatusUpdate

    )

    setSound(newSound)
  }

  useEffect(() => {
    if (song) {
      playCurrentSong();
      notice.notify();
    }
  }, [song])



  const onPlayPausePress = async () => {
    if (!sound) {
      return;
    }
    notice.notify();
    if (isPlaying) {
      await sound.pauseAsync();
    } else {
      await sound.playAsync();
      console.log(song);
    }
  }

  const getProgress = () => {
    if (sound === null || duration === null || position === null) {
      return 0;
    }

    return (position / duration) * 100;
  }

  if (!song) {
    return null;
  }

  
  const fileUrl = song.uri;
  const onDownload = async () => {
    
      // Function to check the platform
      // If Platform is Android then check for permissions.
   
      if (Platform.OS === 'ios') {
        downloadFile();
      } else {
        try {
          const granted = await PermissionsAndroid.request(
            PermissionsAndroid.PERMISSIONS.WRITE_EXTERNAL_STORAGE,
          );
          if (granted === PermissionsAndroid.RESULTS.GRANTED) {
            // Start downloading
            downloadFile();
            console.log('Storage Permission Granted.');
          } else {
            // If permission denied then show alert
            Alert.alert('Error','Storage Permission Not Granted');
          }
        } catch (err) {
          // To handle permission related exception
          console.log("++++"+err);
        }
      }
    };
   
    const downloadFile = () => {
     
      // Get today's date to add the time suffix in filename
      let date = new Date();
      // File URL which we want to download
      let FILE_URL = fileUrl;    
      // Function to get extention of the file url
      let file_ext = getFileExtention(FILE_URL);
     
      file_ext = '.' + file_ext[0];
     
      // config: To get response by passing the downloading related options
      // fs: Root directory path to download
      const { config, fs } = RNFetchBlob;
      let RootDir = fs.dirs.PictureDir;
      let options = {
        fileCache: true,
        addAndroidDownloads: {
          path:
            RootDir+
            '/file_' + 
            Math.floor(date.getTime() + date.getSeconds() / 2) +
            file_ext,
          description: 'downloading file...',
          notification: true,
          // useDownloadManager works with Android only
          useDownloadManager: true,   
        },
      };
      config(options)
        .fetch('GET', FILE_URL)
        .then(res => {
          // Alert after successful downloading
          console.log('res -> ', JSON.stringify(res));
          Alert.alert('File Downloaded Successfully.');
        });
    };
   
    const getFileExtention = fileUrl => {
      // To get the file extension
      return /[.]/.exec(fileUrl) ?
               /[^.]+$/.exec(fileUrl) : undefined;
    };
  

  return (
    <View style={styles.container}>
      <View style={[styles.progress, { width: `${getProgress()}%` }]} />
      <View style={styles.row}>
        <Image source={{ uri: song.imageUri }} style={styles.image} />
        <View style={styles.rightContainer}>
          <View style={styles.nameContainer}>
            <Text style={styles.title}>{song.title}</Text>
            <Text style={styles.artist}>{song.artist}</Text>
          </View>
          <View style={styles.iconsContainer}>
            <TouchableOpacity onPress={onPlayPausePress}>
              <FontAwesome name={isPlaying ? 'pause' : 'play'} size={30} color={"white"} style={{paddingRight: 20}} />
            </TouchableOpacity>
            <TouchableOpacity onPress={onDownload}>
              <AntDesign name={'clouddownload'} size={30} color={"white"} />
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </View>
  )
}

export default PlayerWidget;